import {
  GithubAuthProvider,
  GoogleAuthProvider,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { authService } from "../fbase";
import "../style/Login.css";
import { resetClass } from "./your-utils-file"; // your-utils-file에 resetClass 함수가 있는 파일 경로로 대체해주세요.

const Auth = (props) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [userData, setUserData] = useState(null);
  const [password, setPassword] = useState("");
  const [newAccount, setNewAccount] = useState(true);
  const [error, setError] = useState("");

  const onChange = (event) => {
    const {
      target: { name, value },
    } = event;
    if (name === "email") {
      setEmail(value);
    } else if (name === "password") {
      setPassword(value);
    }
  };

  const handleGoogleLogin = () => {
    const provider = new GoogleAuthProvider();
    signInWithPopup(authService, provider)
      .then((data) => {
        setUserData(data.user);
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleGithubSignIn = () => {
    const provider = new GithubAuthProvider();
    signInWithPopup(authService, provider)
      .then((data) => {
        setUserData(data.user);
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      if (newAccount) {
        let data = await createUserWithEmailAndPassword(
          authService,
          email,
          password
        );
        console.log("createUserWithEmailAndPassword 성공!");
        console.log(data);
      } else {
        let data = await signInWithEmailAndPassword(
          authService,
          email,
          password
        );
        console.log("signInWithEmailAndPassword 성공!");
        setUserData(data.user);
        console.log(data);
      }
    } catch (error) {
      switch (error.code) {
        case "auth/weak-password":
          setError("비밀번호는 6자리 이상이어야 합니다");
          break;
        case "auth/invalid-email":
          setError("잘못된 이메일 주소입니다");
          break;
        case "auth/email-already-in-use":
          setError("이미 가입되어 있는 계정입니다");
          break;
      }
    }
  };

  return (
    <div className="form signup">
      <div className="form-header">
        <div
          className="show-signup"
          onClick={() => {
            let form = document.getElementsByClassName("form")[0];
            resetClass(form, "signin");
            resetClass(form, "reset");
            form.classList.add("signup");
            document.getElementById("submit-btn").innerText = "Sign Up";
          }}
        >
          Sign Up
        </div>
        <div
          className="show-signin"
          onClick={() => {
            let form = document.getElementsByClassName("form")[0];
            resetClass(form, "signup");
            resetClass(form, "reset");
            form.classList.add("signin");
            document.getElementById("submit-btn").innerText = "Sign In";
          }}
        >
          Sign In
        </div>
        <div
          className="show-reset"
          onClick={() => {
            let form = document.getElementsByClassName("form")[0];
            resetClass(form, "signup");
            resetClass(form, "signin");
            form.classList.add("reset");
            document.getElementById("submit-btn").innerText = "Reset password";
          }}
        >
          Reset
        </div>
      </div>
      <div className="arrow"> </div>
      <div className="form-elements">
        <form onSubmit={onSubmit}>
          <div className="form-element">
            <input
              name="email"
              type="email"
              placeholder="Email"
              required
              value={email}
              onChange={onChange}
            />
          </div>
          <br></br>
          <div className="form-element">
            <input
              name="password"
              type="password"
              placeholder="Password"
              required
              value={password}
              onChange={onChange}
            />
          </div>
          <br></br>
          {error}
          <br></br>
          <div className="form-element">
            <input
              type="submit"
              value={newAccount ? "Create Account" : "Sign In"}
            />
          </div>
        </form>

        <div className="form-element">
          <button onClick={handleGoogleLogin}>구글 로그인</button>
          <button onClick={handleGithubSignIn}>깃허브 로그인</button>
        </div>
        <p>로그인 유저: {userData ? userData.displayName : null}</p>
      </div>
    </div>
  );
};

export default Auth;
