import {
  GithubAuthProvider,
  GoogleAuthProvider,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signInWithPopup,
} from "firebase/auth";

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { authService } from "../fbase";
import "./Login.css";

const Auth = (props) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [userData, setUserData] = useState(null);
  const [password, setPassword] = useState("");
  const [newAccount, setNewAccount] = useState(true);
  const [error, setError] = useState("");

  const onChange = (event) => {
    const {
      target: { name, value },
    } = event; // 비구조화 할당
    if (name === "email") {
      setEmail(value);
    } else if (name === "password") {
      setPassword(value);
    }
  };

  const onSubmit = async (event) => {
    event.preventDefault(); // submit 기본 동작(새로고침) 방지
    try {
      if (newAccount) {
        let data = await createUserWithEmailAndPassword(
          authService,
          email,
          password
        );
        console.log("createUserWithEmailAndPassword성공!");
        console.log(data);
      } else {
        //newAccount가 아니라면 sigIn이 되어야할탠데..
        console.log("signInWithEmailAndPassword성공!");
        let data = await signInWithEmailAndPassword(
          authService,
          email,
          password
        );
        console.log(data);
      }
    } catch (error) {
      //에러처리
      // eslint-disable-next-line default-case
      switch (error.code) {
        case "auth/weak-password":
          setError("비밀번호는 6자리 이상이어야 합니다");
          break;
        case "auth/invalid-email":
          setError("잘못된 이메일 주소입니다");
          break;
        case "auth/email-already-in-use":
          setError("이미 가입되어 있는 계정입니다");
          break;
      }
    }
  };

  // const login = async () => {
  //   try {
  //     const curUserInfo = await signInWithEmailAndPassword(
  //       authService,
  //       email,
  //       password
  //     );
  //     // console.log(curUserInfo);
  //     setUserData(curUserInfo.user);
  //   } catch (err) {
  //     // console.log(err.code);
  //     /*
  //     입력한 아이디가 없을 경우 : auth/user-not-found.
  //     비밀번호가 잘못된 경우 : auth/wrong-password.
  //     */
  //   }
  // };

  function handleGoogleLogin() {
    const provider = new GoogleAuthProvider(); // provider 구글 설정
    signInWithPopup(authService, provider) // 팝업창 띄워서 로그인
      .then((data) => {
        setUserData(data.user); // user data 설정
        console.log(data); // console에 UserCredentialImpl 출력
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleGithubSignIn() {
    const provider = new GithubAuthProvider();
    signInWithPopup(authService, provider)
      .then((data) => {
        setUserData(data.user);
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  const toggleAccount = () => setNewAccount((prev) => !prev);
  return (
    <div className="about__container">
      <span onClick={toggleAccount}>회원가입</span>
      <form onSubmit={onSubmit}>
        <input
          name="email"
          type="email"
          placeholder="Email"
          required
          value={email}
          onChange={onChange}
        />
        <br></br>
        <input
          name="password"
          type="password"
          placeholder="Password"
          required
          value={password}
          onChange={onChange}
        />
        <br></br>
        {error}
        <br></br>
        <input
          type="submit"
          value={newAccount ? "Create Account" : "Sign In"}
        />
      </form>
      <div>
        <button onClick={handleGoogleLogin}>구글 로그인</button>
        <button onClick={handleGithubSignIn}>깃허브 로그인</button>
      </div>
      <p>로그인 유저: {userData ? userData.displayName : null}</p>
    </div>
  );
};

export default Auth;
